import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FileSaver from 'file-saver';
import styles from './QRCodeForm.module.css';

const QRCodeForm = React.memo(() => {
  // State hooks to manage component state
  const [ARTICL, setARTICL] = useState([]); // List of ARTICL fetched from the server
  const [selectedARTICL, setSelectedARTICL] = useState(''); // Currently selected ARTICL
  const [searchText, setSearchText] = useState(''); // Text used for filtering ARTICL
  const [loading, setLoading] = useState(false); // Loading indicator
  const [error, setError] = useState(null); // Error message, if any

  useEffect(() => {
    // Effect hook to fetch ARTICL when the component mounts
    const fetchARTICL = async () => {
      try {
        // Fetches the list of 'ARTICL' from the server
        const response = await axios.get('http://192.168.0.22:5000/ARTICLs');
        // Sorts the 'ARTICL' based on 'Descripcion'
        const sortedARTICL = response.data.sort((a, b) => a.DESCRIPCION.localeCompare(b.DESCRIPCION));
        // Sets the sorted 'ARTICL' in the component's state
        setARTICL(sortedARTICL);
      } catch (error) {
        console.error('Error fetching ARTICL:', error.message);
        setError('Error fetching ARTICL. Please try again.');
      }
    };

    // Calls the fetchARTICL function when the component mounts
    fetchARTICL();
  }, []);

  // Function to handle the generation and download of a PDF based on the selectedARTICL
  const handleGeneratePDF = async () => {
    try {
      // Sets the loading state to true
      setLoading(true);

      // Makes an API request using Axios to generate PDF
      const response = await axios.post(
        'http://192.168.0.22:5000/generarpdf',
        {
          ARTICL: selectedARTICL.ARTICL,
          DESCRIPCION: selectedARTICL.DESCRIPCION,
          
        },
        { responseType: 'blob' } // Sets responseType to 'blob' to receive a Blob
      );

      // Creates a Blob object from the response data
      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });

      // Uses FileSaver.js to trigger the download of the PDF
      FileSaver.saveAs(pdfBlob, `${selectedARTICL.ARTICL}.pdf`);

      // Resets form fields and loading state
      setSelectedARTICL('');
      setSearchText('');
      setLoading(false);
      setError(null);
    } catch (error) {
      // Handles errors during PDF generation
      console.error('Error generating PDF:', error.message);
      setError('Error generating PDF. Please try again.');
      setLoading(false);
    }
  };

  // Function to handle the change of selectedARTICL when an option is selected
  const handleARTICLChange = (event) => {
    const selected = ARTICL.find((ARTICL) => ARTICL.ARTICL === event.target.value);
    setSelectedARTICL(selected);
    setSearchText(''); // Resets the search text when an option is selected
  };

  // Creates a filtered list of ARTICL based on the searchText
  const filteredARTICL = ARTICL.filter((ARTICL) =>
    ARTICL.Descripcion.toLowerCase().includes(searchText.toLowerCase())
  );

  // Component JSX
return (
  <div className={styles.container}>
    {/* <h2 className={styles.heading}>Generar PDF con formato para viñetas</h2> */}
    <form className={styles.form}>
      <label className={styles.label}>
        Buscar ARTICL:
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Pendiente su funcion"
          disabled={selectedARTICL !== ''}
          className={styles.input}
        />
      </label>
      <label className={styles.label}>
        Seleccione ARTICL:
        <select
          value={selectedARTICL.ARTICL}
          onChange={handleARTICLChange}
          className={styles.select}
        >
          <option value="" disabled>
            Seleccione ARTICL
          </option>

          {/* Render the sorted options directly */}
          {ARTICL.map(({ ARTICL, Descripcion }) => (
            <option key={ARTICL} value={ARTICL}>
              {Descripcion}
            </option>
          ))}
        </select>
      </label>
      {loading && <p style={{ textAlign: 'center' }}>Cargando...</p>}
      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
      <button
        type="button"
        onClick={handleGeneratePDF}
        disabled={loading}
        className={styles.button}
      >
        Generar PDF con viñetas QR
      </button>
    </form>
  </div>
);

});

// Exports the QRCodeForm component as the default export of the module
export default QRCodeForm;
