// src/OptionsPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OptionsPage = () => {
  const [error, setError] = useState(null);
    const [options, setOptions] = useState([]);

  const [searchTerm, setSearchTerm] = useState('');
  const [filteredOptions, setFilteredOptions] = useState([]);

  useEffect(() => {
    // Fetch data from the API
    axios.get('http://localhost:5000/options')
      .then(response => {
        setOptions(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  useEffect(() => {
    // Filter options based on the search term
    const filtered = options.filter(option =>
      option.ArticuloDetalle.includes(searchTerm) || option.Descripcion.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredOptions(filtered);
  }, [options, searchTerm]);

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    // You can also add additional logic here if needed
  };

  return (
    <div>
      <h1>Imprimir vinetas</h1>
      <label>
        Buscar por ArticuloDetalle o Descripcion:
        <input type="text" value={searchTerm} onChange={handleSearchTermChange} />
      </label>
      <button onClick={handleSearch}>Search</button>

      <h2>Options List:</h2>
      <ul>
        {filteredOptions.map(option => (
          <li key={option.ArticuloDetalle}>
            <p>ArticuloDetalle: {option.ArticuloDetalle}</p>
            <p>Descripcion: {option.Descripcion}</p>
            <p>PrecioUnitario: {option.PrecioUnitario}</p>
            <p>Articulo: {option.Articulo}</p>
            <p>CodigoBarra: {option.CodigoBarra}</p>
            <p>EmpresaDestino: {option.EmpresaDestino}</p>
            <p>Cantidad: {option.Cantidad}</p>
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OptionsPage;
