import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FileSaver from 'file-saver';
import styles from './QRCodeForm.module.css';

const QRCodeForm = () => {
  const [articulos, setArticulos] = useState([]);
  const [selectedArticulo, setSelectedArticulo] = useState('');
  const [searchText, setSearchText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticulos = async () => {
      try {
        const response = await axios.get('http://192.168.0.110:5000/articulos');
        setArticulos(response.data);
      } catch (error) {
        console.error('Error fetching articulos:', error.message);
        setError('Error fetching articulos. Please try again.');
      }
    };

    fetchArticulos();
  }, []);

  const handleGeneratePDF = async () => {
    try {
      setLoading(true);
  
      // Make API request using Axios
      const response = await axios.post(
        'http://192.168.0.110:5000/generarpdf',
        {
          Descripcion: selectedArticulo.Descripcion,
          ArticuloDetalle: selectedArticulo.ArticuloDetalle,
        },
        { responseType: 'blob' } // Set responseType to 'blob' to receive a Blob
      );
  
      // Handle the successful response
      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
  
      // Use FileSaver.js to trigger the download
      FileSaver.saveAs(pdfBlob, `${selectedArticulo.ArticuloDetalle}.pdf`);
  
      // Reset form fields and loading state
      setSelectedArticulo('');
      setSearchText('');
      setLoading(false);
      setError(null);
    } catch (error) {
      // Handle errors
      console.error('Error generating PDF:', error.message);
      setError('Error generating PDF. Please try again.');
      setLoading(false);
    }
  };

  const handleArticuloChange = (event) => {
    const selected = articulos.find((articulo) => articulo.ArticuloDetalle === event.target.value);
    setSelectedArticulo(selected);
    setSearchText(''); // Reset the search text when an option is selected
  };

  const filteredArticulos = articulos.filter((articulo) =>
    articulo.Descripcion.toLowerCase().includes(searchText.toLowerCase())
  );

  /* return (
    <div>
      <h2>Generar PDF con formato para viñetas</h2>
      <form>
        <label>
          Buscar Articulo:
          <input
            type="text" 
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Pendiente su funcion"
            disabled//={selectedArticulo !== ''}
          />
        </label>
        <br />
        <label>
          Seleccione Articulo:
          <select value={selectedArticulo.ArticuloDetalle} onChange={handleArticuloChange}>
            <option value="" disabled>Seleccione Articulo</option>
            {filteredArticulos.map((articulo) => (
              <option key={articulo.ArticuloDetalle} value={articulo.ArticuloDetalle}>
                {articulo.Descripcion}
              </option>
            ))}
          </select>
        </label>
        <br />
        {loading && <p>Cargando...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="button" onClick={handleGeneratePDF} disabled={loading}>
          Generar PDF con viñetas QR
        </button>
      </form>
    </div>
  ); */

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Generar PDF con formato para viñetas</h2>
      <form className={styles.form}>
        <label className={styles.input}>
          Buscar Articulo:
          <input
            type="text"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Pendiente su funcion"
            disabled={selectedArticulo !== ''}
            className={styles.input}
          />
        </label>
        <label className={styles.select}>
          Seleccione Articulo:
          <select
            value={selectedArticulo.ArticuloDetalle}
            onChange={handleArticuloChange}
            className={styles.select}
          >
            <option value="" disabled>
              Seleccione Articulo
            </option>

            //* Render the sorted options directly */
             {articulos.map(({ ArticuloDetalle, Descripcion }) => (
           <option key={ArticuloDetalle} value={ArticuloDetalle}>
             {Descripcion}
           </option>
           )) }         
           
          </select>
        </label>
        {loading && <p style={{ textAlign: 'center' }}>Cargando...</p>}
        {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
        <button
          type="button"
          onClick={handleGeneratePDF}
          disabled={loading}
          className={styles.button}
        >
          Generar PDF con viñetas QR
        </button>
      </form>
    </div>
  );

};

export default QRCodeForm;
