import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FileSaver from 'file-saver';
import styles from './QRCodeForm.module.css';

const QRCodeForm = React.memo(() => {
  // State hooks to manage component state
  const [A, setA] = useState([]); // List of A fetched from the server
  const [selectedA, setSelectedA] = useState(''); // Currently selected A
  const [searchText, setSearchText] = useState(''); // Text used for filtering A
  const [loading, setLoading] = useState(false); // Loading indicator
  const [error, setError] = useState(null); // Error message, if any

  useEffect(() => {
    // Effect hook to fetch A when the component mounts
    const fetchA = async () => {
      try {
        // Fetches the list of 'A' from the server
        const response = await axios.get('http://192.168.0.22:5000/As');
        // Sorts the 'A' based on 'Descripcion'
        const sortedA = response.data.sort((a, b) => a.DESCRIPCION.localeCompare(b.DESCRIPCION));
        // Sets the sorted 'A' in the component's state
        setA(sortedA);
      } catch (error) {
        console.error('Error fetching A:', error.message);
        setError('Error fetching A. Please try again.');
      }
    };

    // Calls the fetchA function when the component mounts
    fetchA();
  }, []);

  // Function to handle the generation and download of a PDF based on the selectedA
  const handleGeneratePDF = async () => {
    try {
      // Sets the loading state to true
      setLoading(true);

      // Makes an API request using Axios to generate PDF
      const response = await axios.post(
        'http://192.168.0.22:5000/generarpdf',
        {
          A: selectedA.A,
          DESCRIPCION: selectedA.DESCRIPCION,
          
        },
        { responseType: 'blob' } // Sets responseType to 'blob' to receive a Blob
      );

      // Creates a Blob object from the response data
      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });

      // Uses FileSaver.js to trigger the download of the PDF
      FileSaver.saveAs(pdfBlob, `${selectedA.A}.pdf`);

      // Resets form fields and loading state
      setSelectedA('');
      setSearchText('');
      setLoading(false);
      setError(null);
    } catch (error) {
      // Handles errors during PDF generation
      console.error('Error generating PDF:', error.message);
      setError('Error generating PDF. Please try again.');
      setLoading(false);
    }
  };

  // Function to handle the change of selectedA when an option is selected
  const handleAChange = (event) => {
    const selected = A.find((A) => A.A === event.target.value);
    setSelectedA(selected);
    setSearchText(''); // Resets the search text when an option is selected
  };

  // Creates a filtered list of A based on the searchText
  const filteredA = A.filter((A) =>
    A.Descripcion.toLowerCase().includes(searchText.toLowerCase())
  );

  // Component JSX
return (
  <div className={styles.container}>
    {/* <h2 className={styles.heading}>Generar PDF con formato para viÃ±etas</h2> */}
    <form className={styles.form}>
      <label className={styles.label}>
        Buscar A:
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Pendiente su funcion"
          disabled={selectedA !== ''}
          className={styles.input}
        />
      </label>
      <label className={styles.label}>
        Seleccione A:
        <select
          value={selectedA.A}
          onChange={handleAChange}
          className={styles.select}
        >
          <option value="" disabled>
            Seleccione A
          </option>

          {/* Render the sorted options directly */}
          {A.map(({ A, Descripcion }) => (
            <option key={A} value={A}>
              {Descripcion}
            </option>
          ))}
        </select>
      </label>
      {loading && <p style={{ textAlign: 'center' }}>Cargando...</p>}
      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
      <button
        type="button"
        onClick={handleGeneratePDF}
        disabled={loading}
        className={styles.button}
      >
        Generar PDF con viÃ±etas QR
      </button>
    </form>
  </div>
);

});

// Exports the QRCodeForm component as the default export of the module
export default QRCodeForm;
