import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QRCodeForm = () => {
  const [articulos, setArticulos] = useState([]);
  const [selectedArticulo, setSelectedArticulo] = useState('');
  const [searchText, setSearchText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [pdfBlob, setPdfBlob] = useState(null);

  useEffect(() => {
    const fetchArticulos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/articulos');
        setArticulos(response.data);
      } catch (error) {
        console.error('Error fetching articulos:', error.message);
        setError('Error fetching articulos. Please try again.');
      }
    };

    fetchArticulos();
  }, []);

  const handleGeneratePDF = async () => {
    try {
      setLoading(true);

      // Make API request using Axios
      const response = await axios.post('http://localhost:5000/generarpdf', {
        Descripcion: selectedArticulo.Descripcion,
        ArticuloDetalle: selectedArticulo.ArticuloDetalle,
      }, { responseType: 'arraybuffer' });

      // Handle the successful response
      const pdfData = new Blob([response.data], { type: 'application/pdf' });
      setPdfBlob(pdfData);

      // Reset form fields and loading state
      setSelectedArticulo('');
      setSearchText('');
      setLoading(false);
      setError(null);
    } catch (error) {
      // Handle errors
      console.error('Error generating PDF:', error.message);
      setError('Error generating PDF. Please try again.');
      setLoading(false);
      setPdfBlob(null);
    }
  };

  const handleArticuloChange = (event) => {
    const selected = articulos.find((articulo) => articulo.ArticuloDetalle === event.target.value);
    setSelectedArticulo(selected);
  };

  const filteredArticulos = articulos.filter((articulo) =>
    articulo.Descripcion.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div>
      <h2>Generar </h2>
      <form>
        <label>
          Buscar Articulo:
          <input
            type="text"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
          />
        </label>
        <br />
        <label>
          Seleccione Articulo:
          <select value={selectedArticulo.ArticuloDetalle} onChange={handleArticuloChange}>
            <option value="" disabled>Seleccione Articulo</option>
            {filteredArticulos.map((articulo) => (
              <option key={articulo.ArticuloDetalle} value={articulo.ArticuloDetalle}>
                {articulo.Descripcion}
              </option>
            ))}
          </select>
        </label>
        <br />
        {loading && <p>Cargando...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {pdfBlob && (
          <div>
            <p>PDF Creado exitosamente!</p>
            <embed src={URL.createObjectURL(pdfBlob)} type="application/pdf" width="600" height="400" />
          </div>
        )}
        <button type="button" onClick={handleGeneratePDF} disabled={loading}>
          Generar PDF con viñetas QR
        </button>
      </form>
    </div>
  );
};

export default QRCodeForm;
