// src/OptionsPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OptionsPage = () => {
  const [options, setOptions] = useState([]);
  const [userId, setUserId] = useState('');
  const [userOption, setUserOption] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch data from the API
    axios.get('http://localhost:5000/options')
      .then(response => {
        setOptions(response.data);
        localStorage.setItem(localStorageKey, JSON.stringify(response.data));
        console.log(`Data stored in localStorage with key: ${localStorageKey}`);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = () => {
    // Find the user option by ID
    const foundOption = options.find(option => option.Articulo.toString() === userId);

    if (foundOption) {
      setUserOption(foundOption);
    } else {
      setUserOption(null);
      alert('Option not found. Please enter a valid ID.');
    }
  };

  // Filter options based on the search term
  const filteredOptions = options.filter(option =>
    option.Descripcion.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Imprimir viñetas</h1>
      <label>
        Ingrese codigo de Articulo:
        <input type="text" value={userId} onChange={handleUserIdChange} />
      </label>
      <br />
      <label>
        Buscar por Descripcion:
        <input type="text" value={searchTerm} onChange={handleSearchTermChange} />
      </label>
      <button onClick={handleSearch}>Search</button>

      {userOption && (
        <div>
          <h2>Result:</h2>
          <p>ID: {userOption.ArticuloDetalle}</p>
          <p>Name: {userOption.Descripcion}</p>
          {/* Add more fields as needed */}
        </div>
      )}

      <h2>Options List:</h2>
      <ul>
        {filteredOptions.map(option => (
          <li key={option.Articulo}>
            ID: {option.Articulo}, Descripcion: {option.Descripcion}
            {/* Add more fields as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OptionsPage;
