// src/OptionsPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import fs from "fs";


const data2 = fa.readFile("./src/data.json");

const SearchBarFilter = () =>{
  const [searchTerm, setSearchTerm] = useState('');
const [filteredData, setFilteredData] = useState(data2);


const handleInputChange = (event) =>{
const {value} = event.target;
setSearchTerm(value);
filterData(value);
};

const filterData = (searchTerm) =>{
  const filteredData = data2.filter((item) =>
  item.EmpresaDestino.toLowerCase().includes(searchTerm.toLowerCase())
  );
setFilteredData(filteredData);
};

return(
  <div>
    <input type='text'
    placeholder='Buscar...'
    value={searchTerm}
    onChange={handleInputChange}/>


    <ul>
      {filteredData.map((item) =>
      (
        <li key={item.Descripcion}>
          {item.EmpresaDestino}</li>
      ))}
    </ul>
  </div>
);
};

export default SearchBarFilter;
