// src/OptionsPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OptionsPage = () => {
  const [options, setOptions] = useState([]);
  const [userId, setUserId] = useState('');
  const [userOption, setUserOption] = useState(null);

  useEffect(() => {
    // Fetch data from the API
    axios.get('http://localhost:5000/options')
      .then(response => {
        setOptions(response.data);
       console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handleSearch = () => {
    // Find the user option by ID
    const foundOption = options.find(option => option.id.toString() === userId);

    if (foundOption) {
      setUserOption(foundOption);
    } else {
      setUserOption(null);
      alert('Option not found. Please enter a valid ID.');
    }
  };

  return (
    <div>
      <h1>Imprimir vinetas</h1>
      <label>
        Ingrese codigo de Articulo: 
        <input type="text" value={userId} onChange={handleUserIdChange} />
      </label>
      <button onClick={handleSearch}>Search</button>
      {userOption && (
        <div>
          <h2>Result:</h2>
          <p>ID: {userOption.id}</p>
          <p>Name: {userOption.name}</p>
          {/* Add more fields as needed */}
        </div>
      )}
    </div>
  );
};

export default OptionsPage;
